mapper.select:

and project_id = #{projectId}

  and s.model_name like concat(#{namePrefix}, '%')

order by s.id desc


and s.model_name = #{name}

  and s.project_id = #{projectId}

for update

mapper.delete:
update model_info
set is_deleted = 1
where id = #{id}

mapper.update:
update model_info
set is_deleted = 0
where id = #{id}

mapper.select:
select id as model_id, model_name, project_id, owner_id, is_deleted, created_time as runtime_created_time, modified_time as runtime_modified_time from model_info where is_deleted = 1
and model_name = #{name}
and project_id = #{projectId}
order by modified_time desc
